function App() {
  let [count, setCount] = useState(0);
  let clickHandler = () => {
    setCount(count + 1);
  };
  let [str, setStr] = useState("this is a message");

  useEffect(() => {
    document.title = count;
  }, [str]);

  let strClickHandler = () => {
    let tempStr = str;
    if (tempStr.length < 25) {
      tempStr = tempStr + "a";
      setStr(tempStr);
      setCount(count + 1);
    } else {
      console.log("");
    }
  };
  return (
    <div>
      <div
        className="button"
        onClick={() => {
          clickHandler();
        }}
      >
        count
      </div>
      <div
        className="button"
        onClick={() => {
          strClickHandler();
        }}
      >
        {str}
      </div>
    </div>
  );
}
```
